INSERT INTO `info_table` (`title`, `head`, `info`) VALUES
('home', ' พืชผักสวนครัว', '&nbsp;&nbsp;&nbsp;&nbsp;ผักเป็นพืชพวกหนึ่งที่ใช้เป็นอาหารประจำวันของมนุษย์มานานแล้ว อาหาร ทุกมื้อของคนไทยไม่เคยขาดผัก ไม่ว่าจะร่ำารวยหรือยากจน ทุกคนก็กินผักทั้งนั้นและผักของ คนไทยก็มีมากมายหลายชนิดที่ไม่เหมือนกับผักของประเทศอื่น ๆ แต่ถ้าจะถามว่าผักคืออะไร ก็พอจะให้ความหมายกว้าง ๆ ว่า พืชอะไรก็ได้ที่เราสามารถเอาส่วนต่าง ๆ ของมันมาบริโภคได้ เช่น ใบ ก้านใบ ลำต้น ดอก ผล ราก ฯลฯ มาใช้เป็นอาหาร โดยที่ส่วนของพืชนั้น จะต้องมีลักษณะอมน้ำ อ่อนนุ่ม ไม่แข็ง ไม่เกี่ยว มีรสค่อนข้างหวาน และที่สำคัญที่สุด คือจะต้องไม่เป็นพิษต่อร่างกาย ดังนั้นผักจึงคลุมไปถึงพืชล้มลุกชนิดเล็ก ๆ จนถึงพืชยืนต้นที่ ขนาดใหญ่ ๆ <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;ผักที่คุ้นเคยกับชีวิตประจำวันของเราที่สุดก็ได้แก่ ผักตำลึง ผักบุ้ง ผักกระเฉด เพราะผักพวกนี้เรามักจะเก็บเอาตามรั้วบ้านหรือตามคนมาทำแกงส้มบ้าง แกงจืดบ้าง ซึ่งก็ เป็นผักพื้นเมืองของคนไทยเราที่มีคุณค่าทางอาหารสูง ผักกาดต่าง ๆ เช่น ผักกาดหัว ผักกาด เขียว ผักกาดขาว คะน้า บวบ น้ำเต้า แตงกวา ผัก ถั่วฝักยาว ก็ล้วนแต่นํามาประกอบ อาหารทั้งสิ้น และที่จะขาดเสียไม่ได้ก็คืออาหารเด็ด ๆ ดังนั้นพริกก็เป็นผักที่ขึ้นหน้าขึ้นตา ที่ใช้เป็นอาหารโดยตรง หรือเป็นเครื่องชูรสควบคู่ไปกับเครื่องชูรสอื่น ๆ เช่น พริกไทย โหระพา ตะไคร้ ใบมะกรูด กระเพรา เป็นต้น แต่บางครั้งเราจะได้ยินว่าเรียกผักแต่ไม่ได้ เป็นผักก็มี เช่น ผักตบชวา ซึ่งเป็นวัชพืช ผักปลาบ และ ผักเป็ดก็เป็นวัชพืชที่นำมาใช้ ทำยา <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;ในทางวิชาการ ผักหมายถึงพืชล้มลุกที่ใช้บริโภคได้ หรือส่วนของพืชที่ปกติ ใช้เพื่อความมุ่งหมายในการปรุงอาหาร ซึ่งก็ได้แก่ส่วนต่าง ๆ ของพืช คือ ราก ลำต้น ใบ ดอก ช่อดอก เมล็ด เป็นต้น พืชอื่น ๆ ที่อยู่นอกเหนือนี้จึงไม่ใช่ผัก เช่น หัวปลีที่นํามา ทําอาหาร ซึ่งเป็นดอกของต้นกล้วย เราถือว่าเป็นไม้ผลไม่ใช่ผัก หรือหน่อไม้ที่นํามาต้ม มาแกงก็เช่นเดียวกันเป็นต้นอ่อนของไผ่ ดังนั้นคําว่าผักในที่นี้ แล้วแต่ผู้บริโภคจะใช้กัน เพราะคนไทยเรารู้จักผักกันมา นานแล้วโดยเฉพาะพวกผักพื้นเมือง ส่วนผักใหม่ ๆ เมื่อประมาณ 30 ปีนี้เอง จนปัจจุบันนี้ เรามีผักแทบทุกชนิดที่ต่างประเทศเขามี เช่น มะเขือเทศ แครอท มันฝรั่ง พริกยักษ์ เป็นต้น'),
('objective', 'วัตถุประสงค์ของเว็บไซต์', '&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1.เพื่อเป็นสื่อให้ความรู้แก่ผู้ที่สนใจที่เกี่ยวกับผักสวนครัว <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2.เพื่อบอกถึงสรรพคุณและประโยชน์ของผักต่างๆ <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3.เพื่อแนะนําการใช้ส่วนต่างๆ ของผักสวนครัวในการประกอบอาหาร <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;4.เพื่อแนะนําวิธีการปลูก  <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;5.เพื่อแนะนําผักสวนครัวของสมาชิกในกลุ่ม  <br><br>\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;6.เพื่อเป็นการฝึกทําเว็บไซต์ ในรายวิชา CSS2208-001 การพัฒนาโปรแกรมประยุกต์ฐานข้อมูลกับเว็บ <br><br>\r\n'),
('taget', 'กลุ่มเป้าหมาย', '<b>&nbsp;&nbsp;&nbsp;&nbsp;1.อายุของกลุ่มเป้าหมาย</b><br><br>\r\n          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- 12 ปี ขึ้นไป<br><br>\r\n          <b>&nbsp;&nbsp;&nbsp;&nbsp;2.การศึกษา</b><br><br>\r\n          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- ประถมศึกษาขึ้นไป<br><br>\r\n          <b>&nbsp;&nbsp;&nbsp;&nbsp;3.ความรู้เกี่ยวกับเทคนิค</b><br><br>\r\n          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- เป็นผู้ใช้งานอินเทอร์เน็ตเบื้องต้นเป็น<br><br>\r\n          <b>&nbsp;&nbsp;&nbsp;&nbsp;4.การแนะนำด้วยภาพ</b><br><br>\r\n          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- เข้าใจสัญลักษณ์ของเว็บไซต์ทั่วไป ตัวอย่างเช่น รูปบ้าน คือ ปุ่ม Home <br><br>');

