INSERT INTO `vegetable_table` (`key_id`, `name`, `bota_name`, `family`, `history`, `nature`, `plan`, `benefit`, `id_card`, `img1`, `img2`) VALUES
('01rose', 'กระเจี๊ยบขาว', 'Hibiscus esculentus Linn. ', 'Malvaceae', 'เป็นพืชที่มีปลูกกันมากในประเทศอินเดีย บังคลาเทศ ประเทศไทยก็ นิยมปลูกกระเจี๊ยบกันมานานแล้วเช่นกัน นอกจากนี้ยังมีปลูกกันในพม่าเรียกว่า “ยูปาดี” ในมาเลเซียเรียกว่า “กะจังเบนติด” ในบอเนียวเหนือเรียกว่า “เกจังไลโอฮิน” และในอินเดีย เรียกว่า “บินดี”', 'กระเจี๊ยบขาว เป็นพืชล้มลุกอายุสั้น ต้นมีความสูง 5-6 ฟุต ดอกมีขนาดใหญ่สีเหลือง ฝักกระเจี๊ยบมีความยาวประมาณ 10 นิ้ว มีเมล็ดค่อนข้างโต ขนาด 1) ฝักที่เหมาะสมสำหรับใช้รับประทาน ควรมีความยาวประมาณ 3 นิ้ว หรือยาวไม่เกิน 4-5 นิ้ว ผักที่ยาวไปกว่านี้จะเป็นเสี้ยนแข็งรับประทานไม่ได้', 'กระเจี๊ยบปลูกด้วยเมล็ด ควรใช้ระยะปลูกระหว่างหลุมห่างกัน 50 ซ.ม. ระยะระหว่างแถวห่างกัน 75-100 ซ.ม. หยอดเมล็ดหลุมละ 3-5 เมล็ด เมื่อกระเจี๊ยบมี อายุได้ 25-30 วัน ถอนแยกให้เหลือหลุมละ 1 ต้น', 'ใช้เป็นผักจิ้มน้ำพริก โดยเอาไปต้มหรือเผา ลอกเอาเปลือกออก มีรสหวาน เนื้อนิ่มและเป็นเรือกๆ นอกจากนั้นยังช้ำทำซุปหรือสตูก็ได้ ในสหรัฐช้ำทำผักแช่แข็ง และบรรจุประป่องอีกด้วย', '01', 'images/ผัก/กระเจี๊ยบ1.png', 'images/ผัก/กระเจี๊ยบ2.png'),
('02kaem', 'กระชาย', 'Baesenbergia bandurata (Roxb.) Schltr ', 'Zingiberaceae', 'มีถิ่นกำเนิดในทวีปเอเชีย มีปลูกกันมากในประเทศอินเดีย ศรีลังกา อินโดนีเซีย อินโดจีน ไทย และมาเลเซีย', 'เป็นพืชผักสมุนไพรที่ให้กลิ่นหอม และมีอายุยืนนาน มี ลำต้นที่เป็นหัวคล้ายหัวเผือกแต่มีขนาดเล็กกว่า อยู่ใต้ดิน หัวแต่ละหัวเชื่อมติดกันเป็นแนวยาวที่โคนของหัวแต่ละหัวจะสร้างรากอ้วนกลมยาวประมาณ 10-15 ซ.ม. มีลักษณะอวบน้ำ เป็นรากที่เก็บอาหาร และมีกลิ่นหอม ต้นที่อยู่ใต้ดินจะชูใบและก้านใบขึ้นเหนือพื้นดิน ประมาณ 30 ซ.ม. ขึ้นได้ดีทั้งในที่โล่งแจ้งและใต้ต้นไม้ใหญ่', 'ไถและพรวนดิน หรือใช้จอบขุดดิน ย่อยดินให้ร่วนโปร่ง ใช้ส่วน ของหัวปลูกลงในดิน กลบดินให้หัวเสมอผิวดิน ควรใช้ระยะปลูกระหว่างต้น 40 ซ.ม. ระหว่างแถว 60 ซ.ม.', 'ใช้ส่วนของรากที่อวบอ้วน ปอกเปลือก แล้วนำมาใช้ปรุงอาหารที่ รู้จักกันมากที่สุดคือ “น้ำยา” กลิ่นหอมของรากกระชายจะดับกลิ่นคาวของเนื้อปลา ซึ่งเป็น ส่วนประกอบจำนวนมากในน้ำยาให้หมดไป', '02', 'images/ผัก/กระชาย1.png', 'images/ผัก/กระชาย2.png'),
('03holy', 'กะเพรา', 'Ocimum sanctum L.', 'Labiatae', 'เป็นพืชที่ปลูกกันในเอเชียตะวันออกเฉียงใต้มานานแล้ว โดยมีจุดประสงค์ ที่จะใช้กลิ่นหอมของใบ ช่วยทำให้อาหารมีกลิ่นหอมน่ารับประทานมากขึ้น นอกจากนั้นยัง ใช้เป็นยารักษาโรคอีกด้วย', 'กะเพราเป็นไม้พุ่มเตี้ย มีความสูงของพุ่มต้นประมาณ 75 ซ.ม. ลำต้นและใบมีขนเล็กน้อย ต้นมีอายุยืนนาน ในประเทศไทยมีอยู่ 2 พันธุ์ คือ\r\nกะเพราขาว และแดง\r\n', 'นิยมปลูกกะเพราด้วยเมล็ดมากกว่ากิ่งปักชํา เมื่อต้นโตและแก่ควรตัด แต่งกิ่งใหม่ กะเพราก็จะแตกกิ่งก้านขึ้นมาใหม่สวยงามเหมือนเดิม \"นิยมปลูกกะเพราเป็น สวนหลังบ้าน โดยปลูกเป็นกลุ่มตอนใดตอนหนึ่งของบ้าน ไม่นิยมปลูกบนแปลงเหมือนพืช ผักสวนครัวอื่น ๆ', 'ใช้ใบสดใบอ่อนประกอบอาหารเพื่อดับกลิ่นคาวและช่วยให้อาหารมี กลิ่นหอม เช่น ไก่ผัดใบกะเพรา เนื้อผัดใบกะเพรา หมูผัดใบกะเพราเป็นต้น ใช้ใส่ในต้มยํา กุ้ง หอย ปู ปลา ใส่ในแกงเลียง มีคุณสมบัติเป็นยาขับลม ขับเหงื่อ และขับเสมหะ', '03', 'images/ผัก/กะเพราควาย1.png', 'images/ผัก/กะเพราควาย2.png'),
('04gala', 'ข่า', 'Languas galanga (L.) Stuntz\r\nชื่อที่เรียกกันในประเทศต่างๆ ลาออส (ชะวา) เลอกวส (มาเลเซีย) ไชมีสจินเจอร์ (อังกฤษ)', '-', 'มีถิ่นกำเนิดในเอเชียตะวันออกเฉียงใต้ มาโคโปโล (ค.ศ. 13) พบว่ามี การปลูกต้นขากันในประเทศจีนตอนใต้และในชะวา นอกจากนั้นขายังเป็นพืชพื้นเมืองตั้งแต่ เทือกเขาหิมาลัยจรดศรีลังกา อินโดจีน ไทย อินโดนีเซีย บอร์เนียว และฟิลิปปินส์ ', 'เป็นพืชผักสมุนไพรที่ให้กลิ่นหอม มีอายุยืนนาน มีลำต้น สีขาวอยู่ใต้ดิน แต่จะชูก้านและใบเหนือพื้นดิน สูงประมาณ 100-150 ซ.ม. ใบสีเขียวเข้มเป็นมัน ลำต้นที่อยู่ใต้ดินนอกจากจะให้กลิ่นหอมแล้วยังมีรสเผ็ดร้อนอีกด้วย เจริญเติบโตได้ ดีในร่มเงาใต้สวนผลไม้และในดินร่วนโปร่ง ต้นที่อยู่ใต้ดินจะให้ตา\r\nดอกและชูก้านดอกบาน เหนือพื้นดิน ดอกสีชมพู\r\n', 'ไถและพรวนดินให้ร่วนโปร่ง ใช้ลำต้นใต้ดินที่มีถึงและใบติดบางส่วน ปลูกระยะปลูกระหว่างหลุมห่างกัน 70 ซ.ม. ระยะระหว่างแถวห่างกัน 1 เมตร', 'เป็นส่วนประกอบของเครื่องแกงเผ็ดชนิดต่าง ๆ ดอกตูมและดอกบาน ใช้ดองเปรี้ยว รับประทานได้ ลำต้นอ่อนของข่าใช้ดับกลิ่นคาวในอาหารที่มีเนื้อสัตว์และเนื้อ ปลา อาจใช้ปลูกประดับบ้านได้ด้วย', '04', 'images/ผัก/ข่า1.png', 'images/ผัก/ข่า2.png'),
('05chao', 'ชะอม', 'Acacia insuanis ', 'Leguminosae', 'เป็นพืชยืนต้น และไม้พื้นเมืองของไทย คนไทยนิยมปลูกชะอมเป็น รั้วบ้าน ซึ่งสามารถเด็ดยอดไปบริโภคเป็นผักได้ทุก ๆ 3 วัน ต้นชะอมหากถูกเด็ดยอดจะ แตกถึงข้างต้นหนาแน่น ชะอมมีหนามตามกิ่งจึงใช้เป็นรั้วบ้านได้ดี มีปลูกมากในจังหวัด นครปฐม ราชบุรี และอำเภอปากช่อง', 'ต้นชะอมเป็นไม้ยืนต้น ตระกูลถั่วที่มีอายุยืนนาน กิ่งอ่อน ยอดอ่อน ใบอ่อน มีกลิ่นหอมฉุนและมีวิตามินเอสูงถึง 32,000 อินเตอร์เนชั่นแนลยูนิต ซึ่งสูงกว่าหัวแครอทที่ฝรั่งเชื่อว่ามีวิตามินเอสูงที่สุด ในบรรดาผักทั้งหลายอีกด้วย ยอดชะอม จัดเป็นอาหารประเภทผักที่มีคุณค่าอาหารสูงยิ่งทั้งวิตามินเอ โปรตีนและไฟเบอร์ที่ร่างกาย ต้องการ', 'ใช้กิ่งตอนหรือกิ่งปักชํา หากปลูกเป็นรั้วควรใช้ระยะปลูกระหว่างต้น ในแถว 50 ซ.ม. ชะอมมี 2 พันธุ์ คือพันธุ์เบาใบเล็ก ต้นเล็ก ยอดขนาดเล็ก และพันธุ์ หนักต้นใหญ่ ใบใหญ่ ชาวสวนนิยมปลูกพันธ์หนักมากกว่า เพราะเก็บยอดขายได้ตลอดทั้งปี', 'ใช้รับประทานเป็นผักจิ้มน้ำพริก ใช้สับปนคลุกกับไข่ทอดเป็นชะอมชุบไข่ทอด ซึ่งมีรสชาติอร่อยมาก', '05', 'images/ผัก/ชะอม1.png', 'images/ผัก/ชะอม2.png'),
('06lemo', 'ตะไคร้', 'Cymbopogon citratus Staph ', 'Gramineae', 'มีถิ่นกำเนิดในทวีปเอเชีย ปลูกกันมากในอินเดีย พม่า มาเลเซีย ไทย อินโดนีเซีย\r\n  ลักษณะโดยทั่วไป เป็นพืชผักสมุนไพรที่มีอายุยืนนาน ขยายพันธุ์ด้วยการใช้ ต้นปลูกตะไคร้มีลำต้นที่แท้จริงประมาณ 4-7 ซ.ม. ลำต้นถูกหุ้มไว้ด้วยกาบใบที่ค่อนข้างยาว ซึ่งอุดมด้วยน้ำมันหอมระเหย ใบยาวแคบ เส้นใบขนานกับก้านใบ ใบตะไคร้ก็อุดมด้วย น้ำมันหอมระเหย มีระบบรากเป็นรากฝอยสามารถยึดจับผิวดินได้ดี มีทรงพุ่มสูงประมาณ 1 เมตร\r\n', 'เป็นพืชผักสมุนไพรที่มีอายุยืนนาน ขยายพันธุ์ด้วยการใช้ ต้นปลูกตะไคร้มีลำต้นที่แท้จริงประมาณ 4-7 ซ.ม. ลำต้นถูกหุ้มไว้ด้วยกาบใบที่ค่อนข้างยาว ซึ่งอุดมด้วยน้ำมันหอมระเหย ใบยาวแคบ เส้นใบขนานกับก้านใบ ใบตะไคร้ก็อุดมด้วย น้ำมันหอมระเหย มีระบบรากเป็นรากฝอยสามารถยึดจับผิวดินได้ดี มีทรงพุ่มสูงประมาณ 1 เมตร', 'ไถและพรวนดินหรือใช้จอบขุดและย่อยดินให้ร่วนโปร่ง แล้วใช้จอบ ขุดหลุมประมาณ 1 ฟุต 1 หลุมปลูกตะไคร้ได้ 4 ต้น ใช้ต้นตะไคร้ที่ประกอบด้วยลำต้น กาบใบและมีใบติดเล็กน้อย ปลูกลงในดิน โดยฝั่งส่วนของต้นให้ลึกจากผิวดินประมาณ 4 - 5 ซ.ม. ใช้ระยะระหว่างต้นห่างกัน 30 ซ.ม. ระยะระหว่างแถวห่างกัน 75 ซ.ม.', 'ใช้ส่วนของลำต้นและกาบใบชั้นในประกอบเครื่องแกงเผ็ด เป็นส่วน ประกอบอาหารที่มีชื่อว่า “ไก่สามอย่าง” ใบใส่ในท้อง หมูหัน แพะ หรือแกะย่างรมควัน เพื่อดับกลิ่นคาวและกลิ่นสาป', '06', 'images/ผัก/ตะไคร้1.png', 'images/ผัก/ตะไคร้2.png'),
('07cucu', 'แตงกวา', 'Cucumis sativus Linn. ', 'Cucurbitaceae', 'มีการปลูกแตงกวาในประเทศอินเดียมาไม่ต่ำกว่า 3,000 ปี และมีการ นําเข้าไปปลูกในประเทศจีนในสมัยราชวงศ์ฮั่น (Han dynasty) 200 ปีก่อนคริสต์ศักราช', 'แตงกวาเป็นพืชอายุสั้น ลำต้นเป็นเถาเลื้อยทอดนอนไปตาม ดิน และมีมือจับเกาะเพื่อไม่ให้เถากลิ้งบิดเป็นเกลียวเมื่อถูกลมพัดแรง แตงกวาพื้นเมืองของ ไทยมีหลายพันธุ์ พันธุ์ที่อายุสั้นที่สุดคือแตงไข่ มีอายุเก็บเกี่ยว 30 วันนับจากวันงอกและมี ช่วงเวลาเก็บเกี่ยวประมาณ 25-30 วัน เถาแตงกวามีความยาวประมาณ 2.5 เมตร แตงกวา มีทั้งดอกตัวเมียและตัวผู้ ดอกตัวเมียมีรังไข่ซึ่งมีลักษณะคล้ายผลขนาดเล็ก ๆ อยู่ใต้ดอก ดอก ตัวผู้นั้นเป็นดอกเดี่ยว ๆ', 'หยอดเมล็ดเป็นหลุม ๆ ละ 5 เมล็ด กลบดินหนาไม่เกิน 1 ซ.ม. ใส่ ปุ๋ยคอกและปุ๋ยวิทยาศาสตร์รองก้นหลุมพร้อมกับหยอดเมล็ด วางปุ๋ยให้ห่างจากเมล็ด 3 นิ้ว ใส่ปุ๋ยคอกหลุมละ 250 กรัม ปุ๋ยวิทยาศาสตร์ 20-10-10 หลุมละ 15 กรัม ใช้ระยะ ปลูกระหว่างหลุมห่างกัน 90 ซ.ม. ระยะระหว่างแถวห่างกัน 2.50 เมตร', 'ใช้รับประทานผลอ่อนเป็นผักสดหรือดองเปรี้ยว', '07', 'images/ผัก/แตงกวา1.png', 'images/ผัก/แตงกวา2.png'),
('08lent', 'ถั่วฝักยาว', 'vigna sinensis sesquipedalis (L.) Fruw ', 'Leguminosae', 'ถั่วฝักยาวที่ปลูกกันทั่วไป มีถิ่นกำเนิดในตะวันออกไกลและในอาฟริกา Vaviov (1951) กล่าวว่า ถั่วฝักยาวมีถิ่นกำเนิดในประเทศจีน', 'ถั่วฝักยาวเป็นพืชตระกูลถั่วที่มีลำต้นเป็นเถาเลื้อยพันไปทางขวา จึงนิยมปลูกถั่วฝักยาวให้ขึ้นไม้ค้าง มีผลการทดลองแสดงให้เห็นว่า ถั่วฝักยาวที่ขึ้นค้างนั้น ให้ผลผลิตสูงกว่าถั่วฝักยาวไม่ขึ้นค้างอย่างมากมายทีเดียว', 'นิยมปลูกเป็นหลุม ควรไถและพรวนดินให้ร่วนโปร่งแล้วจึงขุดหลุม กว้าง 25 ซ.ม. ลึก 10 ซ.ม. ใส่ปุ๋ยคอก 250 กรัม ผสมปุ๋ยวิทยาศาสตร์สูตร 15-15-15 จำนวน 15 กรัม รองก้นหลุม กลบดินทับปุยหนา 2-3 นิ้ว แล้วจึงหยอดเมล็ดหลุมละ 5 เมล็ด แล้วกลบดินหนาไม่เกิน 1 ซ.ม. เมื่อต้นถั่วฝักยาวเริ่มทอดยอดใช้มีดตัดต้นออกให้ เหลือหลุมละ 3 ต้น ใช้ระยะปลูกระหว่างหลุมห่างกัน 60 ซ.ม. ระยะระหว่างแถวห่างกัน 100 ซ.ม. ปักค้างทันทีเมื่อปลูกเสร็จ', 'ให้ฝักสดที่ยังอ่อนรับประทานเป็นผักสด จิ้มน้ำพริก ผัดกับหมู เนื้อ และไก่ใส่ในผัดพริกขิงเป็นต้น', '08', 'images/ผัก/ถั่วฝักยาว1.png', 'images/ผัก/ถั่วฝักยาว2.png'),
('09bean', 'ถั่วพู', 'Psophocarpus tetragonolobus (Stickm.) DC. ', 'Leguminosae', 'ปัจจุบันยังไม่ทราบถิ่นกำเนิดที่แน่นอนของถั่วพู Burkill (1953) เชื่อว่า ถั่วพูมาจากมาดากาสดาร์ Vavilov (1951)เข้าใจว่าถั่วพูมาจากประเทศอินเดียแล้วแพร่ไปยัง พม่า ศรีลังกา มาเลเซีย อินโดนีเซีย และนิวกินี ประเทศไทยก็มีถั่วพูปลูกมานานแล้ว เช่นกัน', 'เป็นพืชตระกูลถั่วที่มีเถาเลื้อยพันเช่นเดียวกับถั่วฝักยาว และ มีอายุยืนนาน มีหัวอยู่ใต้ดิน ในประเทศพม่านิยมกินหัวถั่วพูเป็นอาหาร มีการปลูกถั่วพู เป็นพืชคลุมดินในสวนกล้วยอีกด้วย นิยมปลูกถั่วพูด้วยเมล็ด ถั่วพูจะเจริญจากเมล็ดนาน 3-4 เดือนจึงออกดอก ให้ฝึก ควรปลูกถั่วพูต้นฤดูฝนให้เลื้อยขึ้นรั้ว หรือสร้างร้านให้เลื้อย พอเข้าฤดูแล้งต้นถั่วพูจะตาย', 'ขุดหลุมกว้าง 2.5 ซ.ม. ทำดินในหลุมให้ร่วนโปร่ง หยอดเมล็ดหลุมละ 5 เมล็ด กลบดินหนา 1 ซ.ม. เมื่อเริ่มทอดเถาถอนแยกให้เหลือหลุมละ 3 ต้น', 'ฝึกอ่อนสดใช้รับประทานเป็นผักสดจิ้มน้ำพริก ผัดน้ำมัน เป็นส่วน ผสมของทอดมัน เป็นพักที่ใช้รับประทานกับขนมจีนน้ำพริก', '09', 'images/ผัก/ถั่วพลู1.png', 'images/ผัก/ถั่วพลู2.png'),
('10zucc', 'บวบเหลี่ยม', 'Luffa acutangula Roxbg. ', 'Cucurbitaceae', 'เป็นพืชพื้นเมืองของเอเชียแถบร้อน เข้าใจว่ามีถิ่นกำเนิดในประเทศ อินเดีย และเป็นพืชพื้นเมืองของไทยด้วย', 'เป็นพืชเถาเลื้อยอายุสั้น มีมือจับเกาะ นิยมสร้างร้านให้เลื้อย เช่นเดียวกับบวบงูและบวบหอม บวบเหลี่ยมต่างกับบวบหอมตรงที่ผลมีลักษณะหลายเหลี่ยม สีเขียวแก่ ปลูกได้ทุกฤดูกาล เป็นพืชที่เจริญเติบโตได้รวดเร็วและมีลักษณะการเจริญเติบโต ทางลำต้น การเกิดดอก การผสมพันธุ์ และการเกิดผลคล้ายกับบวบหอมมาก', 'ควรปลูกต้นฤดูฝน โดยการเตรียมหลุม ใส่ปุ๋ย หยอดเมล็ดและถอน แยกเช่นเดียวกับบวบหอม บวบเหลี่ยมให้ผลบวบที่เหมาะสำหรับรับประทานได้เมื่อต้นบวบมี อายุประมาณ 2 เดือนนับจากวันหยอดเมล็ดหรือผลบวบมีความยาวประมาณ 15-20 ซ.ม.', 'ผลอ่อนที่ปอกเปลือกแล้วฝานเป็นชิ้น ๆ ใช้ผัด ทำแกงจืด แกงเลียง หรืออาจลวกด้วยน้ำร้อนหรือต้มให้สุกจิ้มน้ำพริกรับประทานได้เช่นกัน', '10', 'images/ผัก/บวบเหลี่ยม1.png', 'images/ผัก/บวบเหลี่ยม2.png'),
('11gour', 'ผักตำลึง', 'Coconia grandis (L.) Voigt ', 'Cucurbitaceae', 'เป็นพืชพื้นเมืองของอินเดีย ไทยมาเลเซีย มักขึ้นเองตามริมรั้ว นก ชอบกินผลตำลึงสุกและถ่ายไว้ทั่วไป เมล็ดของตำลึงก็จะแพร่ไปโดยนกเป็นส่วนใหญ่', 'เป็นพืชเถาเลื้อยมีมือจับเกาะ ใบมี 5 แฉก ใบกว้าง 2-4 นิ้ว ดอกสีขาว ผลเมื่ออ่อนสีเขียวยาว 3 นิ้ว เมื่อสุกเป็นสีแดง ใบและยอดอ่อนมี ไวตามินเอสูง 18,608 I.U. (อินเตอร์เนชันแนลยูนิต) ซึ่งใกล้เคียงกับหัวแครอท มี ไวตามินซีสูงถึง 30 มิลลิกรัม และมีโปรตีน 1.3 กรัม จากน้ำหนักสด 100 กรัม มีผู้ปลูก ตำลึงในอินเดียและมาเลเซียเพื่อใช้เป็นอาหารประเภทผักสด', '-', 'ใช้ใบและยอดอ่อนผัดน้ำมัน แกงจืดหมู แกงเลียง น้ำคั้นจากใบ มีอะมิเลส (amylase) ซึ่งมีคุณสมบัติย่อยแป้งได้', '11', 'images/ผัก/ตำลึง1.png', 'images/ผัก/ตำลึง2.png'),
('12morn', 'ผักบุ้งจีน', 'Ipomoea aquatica Fors K. ', 'Convolvuraceae', 'เป็นพืชพื้นเมืองของทวีปเอเชียเขตร้อน อาฟริกา และออสเตรเลีย บ้างก็เชื่อว่ามีถิ่นกำเนิดในอินเดีย แล้วแพร่กระจายไปยังเขตร้อนต่าง ๆ ทั่วโลก', 'ผักบุ้งมี 2 พันธุ์ คือ พันธุ์ป่า ต้นสีแดง อมน้ำตาล ดอกสีม่วง และพันธุ์ปลูก ต้นสีเขียวอ่อนดอกสีขาว', 'บุ้งจีนเป็นการปลูกด้วยเมล็ด โดยใช้ระยะระหว่างเมล็ดที่หยอดห่างกัน 2-3 ซ.ม. และระยะระหว่างแถวห่างกัน 10 ซ.ม. กลบดินหนา 1 ซ.ม. โรยทับด้วยปุ๋ยแอมโมเนียมซัลเฟตไร่ละ 25 กิโลกรัม รดน้ำ เช้าเย็น อย่าให้แปลงขาดความชื้น ผักบุ้งจีนจะโตให้เก็บเกี่ยวได้ภายในเวลา 21-22 วัน', 'ใช้ผัดน้ำมันเป็นผักบุ้งไฟแดงรับประทานสดเป็นผักจิ้มน้ำพริกปลาทู มีคุณสมบัติเป็นยาคือ น้ำต้มจากต้นใช้เป็นยาระบาย ใช้ต้นและใบพอกริดสีดวงทวาร ดอกตูม ใช้แก้โรคกลากเกลื้อน', '12', 'images/ผัก/ผักบุ้งจีน1.png', 'images/ผัก/ผักบุ้งจีน2.png'),
('13pepe', 'พริกชี้ฟ้า', 'Capsicum annuum var. acuminatum ', 'Solanaceae', 'มีถิ่นกำเนิดในทวีปอเมริกาใต้ โดยเฉพาะในประเทศเม็กซิโก เป็นต้น กำเนิดของ species annuum อย่างแท้จริง ฝรั่งเรียกพริกชนิดนี้ว่า long cayenne pepper คนไทยเรียกพริกชี้ฟ้า แต่ผลของพริกชนิดนี้กลับชี้ลงดินเป็นพืชอายุยืนและมีรสเผ็ด เมื่ออ่อน เป็นสีเขียวแต่พอแก่แล้วจะเปลี่ยนเป็นสีแดง', '-', 'เพาะเมล็ดในกระบะเพาะหรือแปลงเพาะ เมื่อต้นกล้ามีอายุ 1 เดือน จึงย้ายลงปลูกในไร่หรือแปลงสวนครัว พริกชี้ฟ้าปลูกได้ผลดีตลอดปี มีอายุจากวันงอกจนถึงวันเก็บเกี่ยวประมาณ 70-95 วัน', 'ผลพริกชี้ฟ้าสีเขียว ใช้ทำพริกดองน้ำส้ม ใส่แกงเผ็ด ผัดกับหมู กุ้งผล พริกแห้งสีแดงใช้เป็นเครื่องแกงเผ็ดพะแนงเนื้อ ใช้นั้นเป็นพริกป่นสำหรับใส่ใน ก๋วยเตี๋ยว', '13', 'images/ผัก/พริกชี้ฟ้า1.png', 'images/ผัก/พริก2.png'),
('14pump', 'ฟักทอง', 'Cucurbita moschata (Ducb. ex Kam.) Duch. ex Poirt. ', 'Cucurbitaceae', 'มีถิ่นกำเนิดในอเมริกากลาง ได้มีการขุดค้นพบหลักฐานในประเทศ เม็กซิโก ว่ามีการปลูกฟักทองมาเป็นเวลานานประมาณ 1,400 ปี ก่อนคริสต์ศักราชแล้ว และมีการค้นพบหลักฐานเพิ่มเติมอีกว่า มีการปลูกฟักทองในประเทศเปรูมานานประมาณ 3,000 ปี ก่อนคริสต์ศักราชอีกด้วย และต่อมาก็ได้มีการปลูกแพร่หลายกันทั่วไปในเขตร้อน รวมทั้งเอเชียเขตร้อนด้วย', '-', 'ไถพรวนดินให้ร่วนโปร่งจึงเตรียมหลุมกว้าง 25 - 30 ซ.ม. ลึก ประมาณ 20-25 ซ.ม. หยอดเมล็ดหลุมละ 5 เมล็ด กลบดินทับเมล็ดหนาไม่เกิน 1 ซ.ม. เมื่อต้นฟักทองเริ่มทอดยอดใช้มีดตัดต้นตรงบริเวณผิวดินเหลือไว้เพียงหลุมละ 2-3 ต้น จะ เก็บเกี่ยวผลฟักทองได้เมื่อต้นฟักทองมีอายุหลังจากวันงอก 100 วัน ฟักทองมีเถายาว 6 เมตร ควรใช้ระยะหลุมห่างกัน 1 เมตร ระยะแถวห่างกัน 6 เมตร ควรปลูกต้นเดือนตุลาคม', 'ใช้ผลพักทองแก่ทำขนม เช่น ฟักทองสังขยา ฟักทองแกงบวช ผัดหมูและไข่ ต้มสุกจิ้มเกลือ', '14', 'images/ผัก/ฝักทอง1.png', 'images/ผัก/ฝักทอง2.png'),
('15eggp', 'มะเขือพวง', 'Solanum tormum Swartz.', 'Solanaceae', 'มีแหล่งกำเนิดในอินเดีย พม่า ไทย มะเขือพวงฝรั่งเรียกว่า “Common Asiatic weed” หรือ “วัชชพืชของเอเชีย” เป็นพืชโบราณชนิดหนึ่งที่คนเอเชียปลูกไว้เพื่อ ใช้รับประทานผล คนไทยก็เป็นชนชาติหนึ่งที่นิยมปลูกพืชชนิดนี้ไว้รับประทานผลของมัน', '-', 'เพาะเมล็ดในกระบะเพาะหรือแปลงเพาะ เมื่อต้นมะเขือพวงมีอายุครบ 1 เดือน ก็ย้ายไปปลูกตรงมุมใดมุมหนึ่งของบ้าน มะเขือพวงเป็นพืชที่มีทรงพุ่มขนาดใหญ่ การตัดแต่งกิ่งจะช่วยลดความใหญ่โตของทรงพุ่มลงได้ การปลูกในไร่ควรใช้ระยะระหว่างต้น 2.5-3 เมตร ระยะระหว่างแถว 3-4 เมตร ถ้าจะร่นระยะปลูกให้แคบกว่านี้ อาจทำได้ โดยตัดแต่งกิ่งให้พุ่มเตี้ยลง', 'ผลอ่อนของมะเขือพวงใช้ใส่ลงในแกงเผ็ดช่วยทำให้รสชาติของแกง เผ็ดดีขึ้น  ใช้รับประทานผลอ่อนเป็นผักจิ้มน้ำพริก ใช้ต้นกล้าเป็นต้นตอสำหรับต่อกิ่งกับมะเขือเทศได้ด้วย', '15', 'images/ผัก/มะเขือพวง1.png', 'images/ผัก/มะเขิอพวง2.png'),
('16ocim', 'ใบแมงลัก', 'Ocimum Canum Sima.', 'Labiatae', 'เป็นพืชที่ปลูกกันในเอเชียตะวันออกเฉียงใต้มานานแล้ว ประเทศไทย มีปลูกมากในจังหวัดนครปฐม ปลูกเพื่อเอาเมล็ดมากกว่าใช้ยอดและใบประกอบอาหาร', 'เป็นพืชพุ่มเตี้ยอายุสั้น ต้นมีความสูงตั้งแต่ 45-90 ซ.ม. ใบเป็นรูปใบหอกสีเขียว มีกลิ่นหอม ดอกสีขาว เมล็ดมีเมือกห่อหุ้ม เมื่อนําไปแช่น้ำเมือก หุ้มเมล็ดจะขยายตัวออกมีลักษณะคล้ายวันสีขาวปนเทา ขยายพันธุ์ด้วยการเพาะเมล็ด อย่างเดียว', 'ควรไถและพรวนดินให้ร่วนละเอียด เก็บเศษหญ้าเศษหินออกจาก แปลงปลูกให้หมด หยอดเมล็ดเป็นแถวให้ระยะระหว่างแถวห่างกัน 30 ซ.ม. กลบดินหนา ไม่เกิน 0.5 ซ.ม. เมื่อต้นแมงลักงอกและมีอายุได้ 20 วัน ถอนแยกให้ต้นขึ้นห่างกัน 15-20 ซ.ม.', 'ใบอ่อน ใบสดและยอดใช้เป็นอาหารประเภทผัก รับประทานกับ ขนมจีนน้ำยา เมล็ดนําไปแช่น้ำร้อนใช้รับประทานกับน้ำกะทิเป็นของหวาน ใบแมงลักมี คุณสมบัติเป็นยาช่วยขับลมขับเหงื่อ น้ำคั้นจากใบใช้กินแก้หวัดและหลอดลมอักเสบ', '16', 'images/ผัก/แมงลัก1 (1).png', 'images/ผัก/แมงลัก1 (2).png'),
('17mint', 'สะระแหน่', 'Mentha Trinidis L.ultidis', 'Labiatae', 'สะระแหน่เป็นพืชพื้นเมืองของยุโรป', 'เป็นพืชถาวรที่มีอายุยืนนาน เป็นพุ่มเตี้ยคลุมผิวดิน และ ทอดลำต้นไปตามพื้นดิน ส่วนยอดจะชูตั้งฉากกับพื้นดิน ใบมีกลิ่นหอม', 'ขยายพันธุ์โดยใช้ส่วนยอดของลำต้นบกช ควรใช้ลำต้นส่วนยอดยาว ประมาณ 3-4 นิ้ว ปักชําลงในดินร่วนผสมปุ๋ยหมักหรือปุ๋ยคอก สะระแหน่จะออกรากและ เจริญเติบโตได้ภายในเวลา 1 เดือน การปลูกสะระแหน่ในกระถางแขวน นอกจากใช้เป็น ไม้แขวนประดับอาคารสถานที่ได้แล้วยังเด็ดยอดและใบไปประกอบอาหารได้อีกด้วย สะระแหน่ ต้องการแสงแดดอย่างน้อยครึ่งวัน และระวังอย่าให้ขาดน้ำ', 'ใช้ส่วนของใบและยอดอ่อน ประกอบอาหารประเภทยําเนื้อ ยำหอยแคลง ยําปลากระป๋อง ใส่ในต้มยําโป๊ะแตก ยําเล็บมือนาง จะช่วยดับกลิ่นคาวของเนื้อ หอยและปลาได้อย่างดี', '17', 'images/ผัก/สาระแหน่1.png', 'images/ผัก/สาระแหน่2.png'),
('18swee', 'โหระพา ', 'Ocimum basilicum Linn. ', 'Labiatae', 'เป็นพืชที่มีปลูกกันแพร่หลายในเอเชียตะวันออกเฉียงใต้ ซึ่งได้แก่ ไทย มาเลเซียและอินโดนีเซีย', 'เป็นพืชล้มลุก อายุสั้น มีความสูงของทรง พุ่มไม่เกิน 60 ซ.ม. ใบสีเขียวเข้ม ก้านใบและลำต้นเป็นสีม่วงแดง ใบมีกลิ่นหอมของน้ำมัน หอมระเหย มีผู้นําใบและลำต้นไปกลั่นเอาน้ำมันหอมระเหย ซึ่งมีคุณสมบัติใช้แต่งกลิ่น อาหารเครื่องดื่ม และยาสีฟัน ', 'ขยายพันธุ์โดยใช้กิ่งอ่อนปักชํา หรือใช้เมล็ดเพาะเป็นต้นกล้า แล้วย้ายปลูกก็ได้ ควรใช้ระยะปลูกระหว่างต้น 25 ซ.ม. ระยะระหว่างแถวห่างกัน 50 ซ.ม.', 'ใบอ่อนและใบสด ยอดอ่อน ใช้รับประทานเป็นผักสดใส่ในแกงเผ็ด ช่วยทำให้แกงมีกลิ่นหอม น้ำมันโหระพานอกจากใช้แต่งกลิ่นอาหาร เครื่องดื่มและยาสีฟัน แล้ว ยังใช้เป็นยาไล่ยุงและแมลงวันได้อีกด้วย', '18', 'images/ผัก/โหระพา1.png', 'images/ผัก/โหระพา2.png');

